context:
@Override
public boolean onMenuItemClick(MenuItem item) {
    switch(item.getItemId()) {
        case R.id.menu_pin:
            note.setPinned(!note.isPinned());
            note.setModificationDate(Calendar.getInstance());
            note.save();
            refreshList();
            return true;
        case R.id.menu_trash:
            if (getActivity() != null) {
                ((NotesActivity) getActivity()).trashNote(note);
            }
            return true;
        default:
            return false;
    }
}

